.button {
  font-size: 22px;
  height: 48px;

  border: 0;
  border-radius: 5px;

  margin: 0;
  padding: 5px 30px 5px 30px;

  background: #efefef;
  box-shadow: #666 0 0px 14px -6px;

  transition: all .2s ease;

  cursor: pointer;

  &.fullwidth {
    width: 100%;
  }

  &.primary {
    background: #0077ff;
    color: #fff;
  }
  &.info {
    background: #f03930;
    color: #fff;
  }
  &.danger {
    background: #f03930;
    color: #fff;
  }
  &.warning {
    background: #f03930;
    color: #fff;
  }
  &.success {
    background: #f03930;
    color: #fff;
  }
  &.rounded {
    border-radius: 48px;
  }

  &:hover {
    box-shadow: #666 0 0px 20px -6px;
  }
}
